import React, { useRef} from 'react'
import styles from './App.module.css';
import Display from './components/Display';
import ButtonContainer from './components/ButtonContainer';
export default function App() {
  // const [display, displayItem] = useState('');
  const displayItem = useRef('');
  const onBtnClick=(event)=>{
    // console.log('btn click ',event.target.innerText);
    const btnItem = event.target.innerText;
    console.log(btnItem);
    if(btnItem=== 'AC'){
      displayItem.current('')
    }
    else if(btnItem === 'DEL'){
      const lastDel =displayItem.substring(0,displayItem.length-1);
      displayItem.current(lastDel);
    }
    else if(btnItem === '='){
      const result = eval(displayItem);
      displayItem.current(result);
    }else{
      const newDisplayVal =displayItem + btnItem;
      displayItem.current(newDisplayVal);
    }
  }
  return (
    <>
    <div className={styles.calculator}>
      <Display DisplayVal={displayItem}></Display>
      <ButtonContainer onButtonClick={onBtnClick}></ButtonContainer>
    </div>
    </>
  )
}
